function y_e=pisti_run_2();%use previous result as init% eps forwardglobal V gama0 R0 eps Ch a b c K Alpha; global delta R gg gama;N=2;par_A=1;t1=clock;V=15;%omegaforcing=0.07;gama0=0.01;R0=20;Ch=1600;a=0.3;b=6e-5;c=1e-3;Alpha=0.5;delta(1)=1-par_A(1);delta(2)=1+par_A(1);R=R0.*(1+delta./100)';gama=gama0.*(1+delta./100)';gg=1./gama;%Krun=[0:0.002:0.02];%basename='p100120_';%Krun=[0:0.001:0.01];%basename='p100120a_';%Krun=[0:0.001:0.01];%basename='p100120b_';%Krun=[0.005:0.0003:0.0085];%basename='p100120c_';%Krun=[0:0.001:0.015];%basename='p022311a_';Krun=[0:0.001:0.015];basename='p022311b_';for jj=1:length(Krun)    filename=strcat(basename,num2str(jj),'.mat')    K=Krun(jj);    if (jj==1)        ini=[-1 0.2 0.2 0.5];    else        ini=data_ini(1:2*N);    end    for j=1:1        if (j>1);        ini=data_ini(1:2*N);        end    tt=[0:0.1:5000];    [t,y]=ode45(@odeNi2,tt,ini);    data_ini=y(end,:);    y_e=y(:,[1:2:end]);    ff(1)=get_freq(y_e(:,1),1/(t(2)-t(1)));     ff(2)=get_freq(y_e(:,2),1/(t(2)-t(1)));    ff    temp=y_e-repmat(mean(y_e),length(y_e),1);    save('-mat',filename, 'y_e', 'ff','K'); % save dataendend