function run_ode2global V Ch a b c; global delta Rc gama alpha R2 area2;par_A=1;V=15;base=['m031111'];gama0=0.01;R2=20;Ch=1600;a=0.3;b=6e-5;c=1e-3;alpha=4;%have values 1 or above 1area2=1;%area of the small electrodeini=[-1 0.2 0.2 0.5];delta(1)=1-par_A(1);delta(2)=1+par_A(1);gama=gama0.*(1+delta./100)';%Krun=[200 300 500 600 700 800 900 1000 2000 inf];%par_A=0.5%Krun=[350:50:700 750:100:1000 1250 1500 2000 3000 inf];%par_A=0.5%Krun=[170 200:20:250 260:10:300 320:20:400 440:40:500 600 700 800 1000 2000 inf];%par_A=1Krun=[100 130 150 200 250 300 400 800 inf];%par_A=1;for k=1:length(Krun)    Rc=Krun(k);    tt=[0:0.1:10000];[t,y]=ode45(@odeNi2,tt,ini);data=y(20000:end,1:2:end);filename=strcat(base,'_',num2str(k),'.dat');save ('-ascii',filename,'data');endfile_nn=strcat('C',base);movefile('m*.dat',file_nn)batchrun(base,Krun,'C');file_n=strcat('C',base,'.dat');result_final(file_n,alpha,area2)end