function run_ode2global V Ch a b c; global delta Rc gama alpha R2 area2;par_A=-1;V=15;base=['m031511'];gama0=0.01;R2=20;Ch=1600;a=0.3;b=6e-5;c=1e-3;alpha=2.56;%have values 1 or above 1area2=1;%area of the small electrodeini=[-1 0.2 0.2 0.5];delta(1)=1-par_A(1);delta(2)=1+par_A(1);gama=gama0.*(1+delta./100)'Krun=[34 36 38 40 42.5 60 75 100 200:100:600 inf];%par_A=1;for k=1:length(Krun)    Rc=Krun(k);    tt=[0:0.1:10000];[t,y]=ode45(@odeNi2,tt,ini);data=y(20000:end,1:2:end);filename=strcat(base,'_',num2str(k),'.dat');save ('-ascii',filename,'data');endfile_nn=strcat('A',base);movefile('m*.dat',file_nn)batchrun(base,Krun,'A');file_n=strcat('A',base,'.dat');result_final(file_n,alpha,area2)end