function run_ode2(alphak,nl)global V Ch a b c; global delta Rc gama alpha R2 area2;par_A=1;V=15;base=['m031011'];gama0=0.01;R2=20;Ch=1600;a=0.3;b=6e-5;c=1e-3;alpha=alphak;%have values 1 or above 1area2=1;%area of the small electrodeini=[-1 0.2 0.2 0.5];delta(1)=1-par_A(1);delta(2)=1+par_A(1);gama=gama0.*(1+delta./100)';%Krun=inf;Krun=[50 60 70 100 120 125 130 135 137.5 140 147 160 175 190 200 220 300 500 800 inf];%Krun=[200 300 500 600 700 800 900 1000 2000 inf];for k=1:length(Krun)    Rc=Krun(k);    tt=[0:0.1:2000];[t,y]=ode45(@odeNi2,tt,ini);data=y(10000:end,1:2:end);filename=strcat(base,'_',num2str(k),'.dat');save ('-ascii',filename,'data');%Fre=findfre(filename,10,2);%Fre_diff(k)=1000*abs(diff(Fre))%if Fre_diff(1)<30 %   break%endendbatchrun(base,Krun,nl);file_n=strcat(nl,base,'.dat');result_final(file_n,alpha,area2)end